// manager.js

/**
 * Disables the right-click context menu to deter basic inspection.
 */
function disableContextMenu() {
    document.addEventListener('contextmenu', event => event.preventDefault());
}

/**
 * Disables common developer tools keyboard shortcuts.
 */
function disableDevToolsShortcuts() {
    document.addEventListener('keydown', function (e) {
        // Block F12
        if (e.keyCode === 123) {
            e.preventDefault();
        }
        // Block Ctrl+Shift+I (or Cmd+Option+I on Mac)
        if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.keyCode === 73) {
            e.preventDefault();
        }
        // Block Ctrl+Shift+J (or Cmd+Option+J on Mac)
        if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.keyCode === 74) {
            e.preventDefault();
        }
        // Block Ctrl+U (or Cmd+Option+U on Mac)
        if ((e.ctrlKey || e.metaKey) && e.keyCode === 85) {
            e.preventDefault();
        }
    });
}

/**
 * Periodically clears the console and displays a warning message.
 * Note: This is a deterrent and can be bypassed by a determined user.
 */
function runConsoleDeterrent() {
    setInterval(() => {
        console.clear();
        console.log("%cInspecting this area is not allowed.", "color:red; font-size:16px;");
    }, 3000); // Runs every 3 seconds
}

/**
 * Initializes all client-side security measures.
 */
export function initializeAppSecurity() {
    disableContextMenu();
    disableDevToolsShortcuts();
    runConsoleDeterrent();
    console.log("Client-side security manager initialized.");
}

/**
 * Determines the initial page to load and executes the loading function.
 * It checks if the main content area is empty and then decides the page
 * based on URL hash, localStorage, or a default value.
 * @param {string} userId - The current user's ID.
 * @param {function} loadPageCallback - The function to call to load the page, expecting (page, userId, addToHistory).
 */
export function manageInitialPageLoad(userId, loadPageCallback) {
    const defaultPage = "home";

    // Determine page load priority: URL hash > localStorage > default.
    const initialHash = window.location.hash.substring(1);
    const pageToLoad = initialHash || localStorage.getItem("lastPage") || defaultPage;

    // Load the page without adding a new entry to the browser's history.
    loadPageCallback(pageToLoad, userId, false);
    return pageToLoad;
}

styles.css.content:
* {
    margin: 0;
    padding: 0;
    text-decoration: none;
    list-style-type: none;
    font-family: "Optimistic";
    box-sizing: border-box;
}

::-webkit-scrollbar {
    display: none;
}

@font-face {
    font-family: 'Optimistic';
    src: url('./Assets/Fonts/Optimistic_Text_A_Md.ttf');
}

:root {
    --header-height: 60px;
    --nav-height: 60px;
}

/* ---------------- Body ---------------- */
html {
    background: #f4f5f7;
    /* Light background */
    color: #1c1c1e;
    /* Dark text */
    font-family: "Optimistic";
    height: 100%;
}

body {
    /* Body will now inherit background and color from html */
    font-family: "Optimistic";
    height: 100%;
    position: relative; /* Needed for animation container */
    overflow-x: hidden; /* Prevent horizontal scroll from animation */
}

/* ---------------- Animated Background ---------------- */
.animated-background {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
    background: linear-gradient(135deg, #f4f5f7 0%, #e8eaf6 100%);
    overflow: hidden;
}

.animated-background::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background-image: 
        radial-gradient(circle at 20% 80%, rgba(14, 99, 156, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(76, 175, 80, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 40% 40%, rgba(255, 193, 7, 0.1) 0%, transparent 50%);
    animation: floatingPattern 20s ease-in-out infinite;
}

.animated-background::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        repeating-linear-gradient(
            45deg,
            transparent,
            transparent 35px,
            rgba(14, 99, 156, 0.03) 35px,
            rgba(14, 99, 156, 0.03) 70px
        );
    animation: slidePattern 30s linear infinite;
}

@keyframes floatingPattern {
    0%, 100% { transform: translate(0, 0) rotate(0deg); }
    25% { transform: translate(-5px, -10px) rotate(1deg); }
    50% { transform: translate(10px, 5px) rotate(-1deg); }
    75% { transform: translate(-10px, 10px) rotate(0.5deg); }
}

@keyframes slidePattern {
    0% { transform: translateX(0); }
    100% { transform: translateX(70px); }
}

/* ---------------- Header ---------------- */
header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: var(--header-height);
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #ffffff;
    color: #1c1c1e;
    border-bottom: 1px solid #e0e0e0;
    z-index: 1000;
    padding: 8px 0;
}

.app-name {
    font-size: 25px;
    font-weight: bold;
    color: #0e639c;
    text-align: center;
    width: 100%;
}

/* ---------------- Main ---------------- */
main {
    position: relative;
    top: var(--header-height);
    padding: 16px;
    padding-bottom: calc(var(--nav-height) + 20px);
    min-height: calc(100vh - var(--header-height) - var(--nav-height));
    overflow-y: auto;
}

/* ---------------- Page Transitions ---------------- */
.page-fade-out {
    animation: fadeOut 0.2s ease-out forwards;
}

.page-fade-in {
    animation: fadeIn 0.3s ease-in forwards;
}

@keyframes fadeOut {
    from {
        opacity: 1;
        transform: translateY(0);
    }

    to {
        opacity: 0;
        transform: translateY(10px);
    }
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* ---------------- Bottom Navigation ---------------- */
.bottom-nav {
    position: fixed;
    bottom: 0;
    width: 100%;
    background-color: #ffffff;
    display: flex;
    justify-content: space-around;
    align-items: center;
    padding: 8px 0;
    z-index: 1000;
    height: var(--nav-height);
}

.bottom-nav button {
    background: none;
    border: none;
    color: #555;
    font-size: 10px;
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer;
}

.bottom-nav .icon {
    width: 22px;
    height: 22px;
    margin-bottom: 2px;
    background-color: #555;
    /* Default inactive color */
    -webkit-mask-size: contain;
    mask-size: contain;
    -webkit-mask-position: center;
    mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-repeat: no-repeat;
    transition: background-color 0.3s ease;
}

.icon-home {
    -webkit-mask-image: url(./Assets/Icons/house.svg);
    mask-image: url(./Assets/Icons/house.svg);
}

.icon-history {
    -webkit-mask-image: url(./Assets/Icons/bar-chart.svg);
    mask-image: url(./Assets/Icons/bar-chart.svg);
}

.icon-subscriptions {
    -webkit-mask-image: url(./Assets/Icons/gem.svg);
    mask-image: url(./Assets/Icons/gem.svg);
}

.icon-profile {
    -webkit-mask-image: url(./Assets/Icons/user.svg);
    mask-image: url(./Assets/Icons/user.svg);
}

.icon-insights {
    -webkit-mask-image: url(./Assets/Icons/insights.svg);
    /* You need to add this icon */
    mask-image: url(./Assets/Icons/insights.svg);
}

.icon-edit {
    -webkit-mask-image: url(./Assets/Icons/edit.svg);
    mask-image: url(./Assets/Icons/edit.svg);
}

/* Hide tier-gated nav buttons by default to prevent flash of content */
.bottom-nav button[data-tier] {
    display: none;
}

.bottom-nav button.active {
    color: #0e639c;
}

.bottom-nav button.active .icon {
    background-color: #0e639c;
    /* Active color */
}

.bottom-nav button:hover .icon {
    animation: shake 0.3s ease-in-out;
}

@keyframes shake {
    0% {
        transform: translateX(0);
    }

    25% {
        transform: translateX(-3px);
    }

    50% {
        transform: translateX(3px);
    }

    75% {
        transform: translateX(-3px);
    }

    100% {
        transform: translateX(0);
    }
}

/* ---------------- Page Titles ---------------- */
.page-title {
    font-size: 20px;
    margin-bottom: 12px;
    font-weight: bold;
    color: #0e639c;
    text-align: center;
}

/* ---------------- Search Bar ---------------- */
.search-container {
    margin-bottom: 16px;
}

.search-wrapper {
    position: relative;
    background-color: #fff;
    border-radius: 8px;
    border: 1px solid #ccc;
    transition: border-color 0.3s ease;
}

.search-wrapper:focus-within {
    border-color: #0e639c;
}

#predictionSearch {
    width: 100%;
    padding: 10px 15px;
    border: none;
    background-color: transparent;
    color: #333;
    font-size: 14px;
    position: relative;
    z-index: 2;
}

#predictionSearch:focus {
    outline: none;
}

#search-ghost-text {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    padding: 10px 15px;
    font-size: 14px;
    color: #ccc;
    z-index: 1;
    pointer-events: none;
    white-space: nowrap;
    overflow: hidden;
}

#predictionSearch {
    padding-right: 35px; /* Make space for clear button */
}

#search-clear-btn {
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    font-size: 22px;
    color: #999;
    cursor: pointer;
    padding: 0 5px;
    line-height: 1;
    display: none; /* JS will toggle this */
}

/* ---------------- Cards ---------------- */
.card {
    background: #ffffff;
    border-radius: 10px;
    padding: 16px;
    margin-bottom: 16px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.08);
}

/* ---------------- Subscription Cards ---------------- */
.pricing-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
    gap: 16px;
}

.subscription-card {
    background: #ffffff;
    border-radius: 12px;
    padding: 20px;
    text-align: center;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    transition: transform 0.2s ease, border 0.2s ease;
}

.subscription-card:hover {
    transform: translateY(-4px);
}

.subscription-card.free {
    border: 2px solid #4caf50;
}

.subscription-card.premium {
    border: 2px solid #2196f3;
}

.subscription-card.vip {
    border: 2px solid #9c27b0;
}

.subscription-card.vvip {
    border: 2px solid #ff9800;
}

.subscription-card h2 {
    font-size: 18px;
    color: #0e639c;
    margin-bottom: 8px;
}

.subscription-card .price {
    font-size: 16px;
    font-weight: bold;
    margin: 8px 0;
    color: #333;
}

.subscription-card ul {
    padding: 0;
    margin: 12px 0;
    text-align: left;
}

.subscription-card ul li {
    margin: 6px 0;
    padding-left: 20px;
    position: relative;
    font-size: 14px;
    color: #555;
}

.subscription-card ul li::before {
    content: "âœ”";
    color: #0e639c;
    position: absolute;
    left: 0;
}

.subscription-card .subscribe-btn {
    margin-top: auto;
    padding: 8px 16px;
    border-radius: 8px;
    background: #0e639c;
    color: #fff;
    font-weight: bold;
    text-decoration: none;
    transition: background 0.3s ease;
}

.subscription-card .subscribe-btn:hover {
    background: #0b4f7a;
}

/* Current Subscription Banner */
.current-tier {
    background: #ffffff;
    border: 2px solid #0e639c;
    border-radius: 10px;
    padding: 12px 16px;
    margin-bottom: 16px;
    text-align: center;
    font-size: 15px;
    font-weight: bold;
    color: #333;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.08);
}

.subscription-card.is-current-plan {
    transform: scale(1.02);
    box-shadow: 0 0 15px rgba(14, 99, 156, 0.5);
}

.current-tier strong {
    color: #0e639c;
}

/* ---------------- Profile ---------------- */
.profile-container {
    display: flex;
    flex-direction: column;
    gap: 16px;
    padding: 8px 0;
}

.profile-card {
    background: #ffffff;
    border-radius: 12px;
    padding: 16px;
    display: flex;
    flex-direction: column;
    gap: 8px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.08);
}

.profile-card h2 {
    font-size: 18px;
    color: #0e639c;
    margin-bottom: 4px;
}

.profile-card p {
    font-size: 14px;
    color: #666;
    margin: 0;
}

/* Profile avatar / username display */
.profile-header {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
    margin-bottom: 16px;
}

.profile-avatar {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background: #444;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 28px;
    font-weight: bold;
    color: #fff;
    border: 2px solid #0e639c;
    cursor: pointer;
    overflow: hidden;
}

.profile-avatar-img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.tier-expiry-date {
    font-size: 13px;
    color: #555;
    margin-top: 4px;
    display: none;
    /* Hidden by default */
}

.profile-username {
    font-size: 20px;
    font-weight: bold;
    color: #111;
}

.profile-email {
    font-size: 14px;
    color: #666;
}

.username-container {
    display: flex;
    align-items: center;
    gap: 8px;
}

.edit-icon {
    cursor: pointer;
    width: 16px;
    height: 16px;
    background-color: #555;
    transition: background-color 0.2s ease;
    -webkit-mask-size: contain;
    mask-size: contain;
    -webkit-mask-position: center;
    mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-repeat: no-repeat;
    -webkit-mask-image: url(./Assets/Icons/edit.svg);
    mask-image: url(./Assets/Icons/edit.svg);
}

.icon-referral {
    -webkit-mask-image: url(./Assets/Icons/referral.svg);
    mask-image: url(./Assets/Icons/referral.svg);
}

.edit-icon:hover {
    background-color: #0e639c;
}

.dark-mode .edit-icon {
    background-color: #d4d4d4; /* Standard dark mode inactive color */
}

/* Profile Stats */
.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
    gap: 16px;
    text-align: center;
    margin-top: 12px;
}

.stat-item {
    display: flex;
    flex-direction: column;
}

.stat-label {
    font-size: 13px;
    color: #666;
    margin-bottom: 4px;
}

.stat-value {
    font-size: 16px;
    font-weight: bold;
    color: #333;
}

.dark-mode .stat-label {
    color: #bbb;
}

.dark-mode .stat-value {
    color: #eee;
}

/* Profile Settings */
.setting-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 8px 0;
}

/* Profile Card Buttons */
.profile-card button {
    margin-top: 8px;
    padding: 8px 16px;
    border-radius: 8px;
    color: #fff;
    font-weight: bold;
    border: none;
    cursor: pointer;
    transition: background 0.3s ease;
}

.danger-zone-buttons {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

#logoutBtn,
#resetStorage {
    background: #d9534f;
}

#logoutBtn:hover,
#resetStorage:hover {
    background: #c9302c;
}

/* Toggle Switch */
.switch {
    position: relative;
    display: inline-block;
    width: 50px;
    height: 24px;
}

.switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

.slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #ccc;
    transition: 0.4s;
    border-radius: 34px;
}

.slider::before {
    position: absolute;
    content: "";
    height: 18px;
    width: 18px;
    left: 3px;
    bottom: 3px;
    background-color: white;
    transition: 0.4s;
    border-radius: 50%;
}

input:checked+.slider {
    background-color: #0e639c;
}

input:checked+.slider::before {
    transform: translateX(26px);
}



/* ---------------- Modal ---------------- */
.modal {
    display: none;
    position: fixed;
    z-index: 2000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.6);
    justify-content: center;
    align-items: center;
}

.modal-content {
    background: #ffffff;
    color: #333;
    padding: 20px;
    border-radius: 12px;
    width: 85%;
    max-width: 400px;
    text-align: center;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.15);
}

.modal-content input {
    width: 100%;
    padding: 10px;
    margin-top: 10px;
    border: 1px solid #ccc;
    border-radius: 6px;
}

.modal-actions {
    margin-top: 15px;
    display: flex;
    justify-content: space-around;
    gap: 10px;
}

.modal-actions button {
    padding: 8px 16px;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-size: 14px;
}

.modal-actions .btn-secondary {
    background: #6c757d;
    color: white;
}

.modal-actions .btn-secondary:hover {
    background: #5a6268;
}

.modal-actions .btn-primary {
    background: #0e639c;
    color: white;
}

.modal-actions .btn-primary:hover {
    background: #0b4f7a;
}

.modal-actions .btn-danger {
    background: #d9534f;
    color: white;
}

.modal-actions .btn-danger:hover {
    background: #c9302c;
}

/* ---------------- History Page ---------------- */
/* History Container */
.history-container {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

/* Shared Data Card Styles */
.history-card,
.prediction-card {
    border: 2px solid;
    border-color: #e0e0e0;
    border-radius: 10px;
    padding: 15px;
    background-color: #ffffff;
    color: #1c1c1e;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.08);
}

.history-title {
    font-size: 1.2rem;
    margin-bottom: 0.4rem;
    color: #111;
}

.history-detail {
    font-size: 1rem;
    color: #444;
}

.history-time {
    font-size: 0.9rem;
    color: #777;
    margin-bottom: 0.8rem;
}

/* Result Styles */
.history-result {
    margin-top: 0.6rem;
    font-weight: bold;
    padding: 4px 8px;
    border-radius: 6px;
    display: inline-block;
}

.history-result.win {
    color: #4caf50;
    border: 2px solid #4caf50;
}

.history-result.loss {
    color: #f44336;
    border: 2px solid #f44336;
}

.history-result.pending {
    color: #ff9800;
    border: 2px solid #ff9800;
}

/* Tab Content */
.history-section .tab-content {
    display: none;
}

.history-section .tab-content.active {
    display: block;
}

/* ---------------- Tabs ---------------- */
.tab-container,
.history-tabs {
    display: flex;
    justify-content: center;
    gap: 12px;
    margin-bottom: 16px;
}

.tab-btn {
    padding: 8px 20px;
    border-radius: 8px;
    border: 2px solid #0e639c;
    background: #f1f3f5;
    color: #0e639c;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
}

.tab-btn.active {
    background: #0e639c;
    color: #fff;
}

.tab-btn:hover {
    background: #0b4f7a;
    color: #fff;
    border-color: #0b4f7a;
}

/* Show/Hide containers */
.pricing-container {
    display: none;
    /* hidden by default */
}

.pricing-container.active {
    display: grid;
}

/* HomePage */
.predictions-container {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.history-card:hover,
.prediction-card:hover {
    transition: transform 0.2s ease;
    transform: translateY(-4px);
}

/* Match Title */
.match-title {
    font-size: 1.2rem;
    margin-bottom: 0.4rem;
    color: #111;
}

/* Prediction Details */
.prediction-detail {
    font-size: 1rem;
    color: #444;
}

.odds {
    font-size: 0.95rem;
    margin: 0.2rem 0;
    color: #e8590c;
    /* Adjusted for light theme contrast */
}

.match-time {
    font-size: 0.9rem;
    color: #777;
    margin-bottom: 0.8rem;
}

/* Confidence Section */
.confidence {
    margin-top: 0.6rem;
}

.confidence span {
    display: block;
    margin-bottom: 0.4rem;
    font-size: 0.9rem;
    color: #555;
}

.confidence-bar {
    width: 100%;
    height: 8px;
    background: #e9ecef;
    border-radius: 5px;
    overflow: hidden;
    margin-top: 5px;
}

.confidence-fill {
    height: 100%;
}

/* High, Medium, Low confidence borders are fine as they are */

/* High Confidence */
.prediction-card.high-confidence {
    border-color: #4caf50;
    --tier-color: #4caf50;
}

.prediction-card.high-confidence .confidence-fill {
    background: linear-gradient(90deg, #388e3c, #4caf50);
}

/* Medium Confidence */
.prediction-card.medium-confidence {
    border-color: #ff9800;
    --tier-color: #ff9800;
}

.prediction-card.medium-confidence .confidence-fill {
    background: linear-gradient(90deg, #f57c00, #ff9800);
}

/* Low Confidence */
.prediction-card.low-confidence {
    border-color: #f44336;
    --tier-color: #f44336;
}

.prediction-card.low-confidence .confidence-fill {
    background: linear-gradient(90deg, #d32f2f, #f44336);
}

/* AI Pick Styling */
.ai-pick {
    border: 2px solid var(--tier-color);
    padding: 4px 8px;
    border-radius: 6px;
    font-weight: bold;
    display: inline-block;
    color: var(--tier-color);
    /* optional: makes text match tier */
}

.card-animation {
    animation: card-enter 0.4s ease-out forwards;
}

@keyframes card-enter {
    from {
        opacity: 0;
        transform: translateY(15px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.dark-mode #search-clear-btn:hover {
