    font-size: 14px;
}

.no-results-message {
    text-align: center;
    color: #777;
    padding: 20px;
    display: none;
    /* Hidden by default */
}

/* ---------------- Pull to Refresh ---------------- */
#refresh-indicator {
    position: fixed;
    top: calc(var(--header-height) + 15px);
    left: 50%;
    transform: translateX(-50%) scale(0);
    width: 50px;
    height: 50px;
    background: linear-gradient(135deg, #0e639c 0%, #4caf50 50%, #ff9800 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: 0 6px 20px rgba(14, 99, 156, 0.3);
    border: 3px solid rgba(255, 255, 255, 0.2);
    font-size: 24px;
    color: white;
    opacity: 0;
    z-index: 10;
}

#refresh-indicator.transitioning {
    transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1), opacity 0.3s ease;
}

#refresh-indicator.refreshing {
    animation: pulse 2s ease-in-out infinite;
    background: linear-gradient(135deg, #4caf50 0%, #ff9800 50%, #0e639c 100%);
    box-shadow: 0 8px 25px rgba(76, 175, 80, 0.4);
}

#refresh-indicator.refreshing span {
    animation: spin 1s linear infinite;
    transform: scale(1.1);
}

@keyframes pulse {
    0%, 100% { 
        transform: translateX(-50%) scale(1); 
        box-shadow: 0 6px 20px rgba(14, 99, 156, 0.3);
    }
    50% { 
        transform: translateX(-50%) scale(1.05); 
        box-shadow: 0 8px 30px rgba(76, 175, 80, 0.5);
    }
}

/* ---------------- Dark Mode Overrides ---------------- */
.dark-mode {
