/* /Auth/auth.css */

/* --- Global & Font --- */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

@font-face {
    font-family: 'Optimistic';
    src: url('../Assets/Fonts/Optimistic_Text_A_Md.ttf');
}

/* --- Body & Theme --- */
body {
    font-family: "Optimistic", sans-serif;
    background: #f4f5f7;
    color: #1c1c1e;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    padding: 20px;
    position: relative; /* For background animation positioning */
    overflow: hidden; /* Hide animation overflow */
}

/* --- Auth Container & Card --- */
.auth-container {
    width: 100%;
    max-width: 420px;
}

.auth-card {
    background: #ffffff;
    border-radius: 12px;
    padding: 24px;    
    border: 2px solid #0e639c;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.08); /* Standard app shadow */
    position: relative; /* For theme toggle positioning */
    text-align: center;
}

.auth-card {
    transition: opacity 0.5s ease-out, transform 0.5s ease-out;
}
.auth-card.fade-out {
    opacity: 0;
    transform: translateY(-20px);
}

.auth-title {
    font-size: 32px;
    font-weight: bold;
    color: #0e639c;
    margin-bottom: 8px;
}

.auth-subtitle {
    font-size: 16px;
    color: #555;
    margin-bottom: 24px;
}

/* --- Form & Inputs --- */
.auth-form {
    display: flex;
    flex-direction: column;
    gap: 16px;
}

.input-wrapper {
    position: relative;
}

.input-icon {
    position: absolute;
    left: 12px;
    top: 50%;
    transform: translateY(-50%);
    width: 18px;
    height: 18px;
    background-color: #555; /* Standard inactive icon color */
    -webkit-mask-size: contain;
    mask-size: contain;
    -webkit-mask-position: center;
    mask-position: center;
    -webkit-mask-repeat: no-repeat;
    z-index: 2; /* Ensure icon is above the input field */
    mask-repeat: no-repeat;
