// manager.js

/**
 * Disables the right-click context menu to deter basic inspection.
 */
function disableContextMenu() {
    document.addEventListener('contextmenu', event => event.preventDefault());
}

/**
 * Disables common developer tools keyboard shortcuts.
 */
function disableDevToolsShortcuts() {
    document.addEventListener('keydown', function (e) {
        // Block F12
        if (e.keyCode === 123) {
            e.preventDefault();
        }
        // Block Ctrl+Shift+I (or Cmd+Option+I on Mac)
        if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.keyCode === 73) {
            e.preventDefault();
        }
        // Block Ctrl+Shift+J (or Cmd+Option+J on Mac)
        if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.keyCode === 74) {
            e.preventDefault();
        }
        // Block Ctrl+U (or Cmd+Option+U on Mac)
        if ((e.ctrlKey || e.metaKey) && e.keyCode === 85) {
            e.preventDefault();
        }
    });
}

/**
 * Periodically clears the console and displays a warning message.
 * Note: This is a deterrent and can be bypassed by a determined user.
 */
function runConsoleDeterrent() {
    setInterval(() => {
        console.clear();
        console.log("%cInspecting this area is not allowed.", "color:red; font-size:16px;");
    }, 3000); // Runs every 3 seconds
}

/**
 * Initializes all client-side security measures.
 */
export function initializeAppSecurity() {
    disableContextMenu();
    disableDevToolsShortcuts();
    runConsoleDeterrent();
    console.log("Client-side security manager initialized.");
}

/**
 * Determines the initial page to load and executes the loading function.
 * It checks if the main content area is empty and then decides the page
 * based on URL hash, localStorage, or a default value.
 * @param {string} userId - The current user's ID.
 * @param {function} loadPageCallback - The function to call to load the page, expecting (page, userId, addToHistory).
 */
export function manageInitialPageLoad(userId, loadPageCallback) {
    const defaultPage = "home";

    // Determine page load priority: URL hash > localStorage > default.
    const initialHash = window.location.hash.substring(1);
    const pageToLoad = initialHash || localStorage.getItem("lastPage") || defaultPage;

    // Load the page without adding a new entry to the browser's history.
    loadPageCallback(pageToLoad, userId, false);
    return pageToLoad;
}

styles.css.content:
* {
    margin: 0;
    padding: 0;
    text-decoration: none;
    list-style-type: none;
    font-family: "Optimistic";
    box-sizing: border-box;
}

::-webkit-scrollbar {
    display: none;
}

@font-face {
    font-family: 'Optimistic';
    src: url('./Assets/Fonts/Optimistic_Text_A_Md.ttf');
}

:root {
    --header-height: 60px;
    --nav-height: 60px;
    --max-width: 1200px;
    --container-padding: 20px;
    --mobile-padding: 15px;
}

/* ---------------- Body ---------------- */
html {
    background: #f8f9fc;
    /* Enhanced light background - more vibrant than dull #f4f5f7 */
    color: #1a1a1c;
    /* Slightly deeper text color for better contrast */
    font-family: "Optimistic";
    height: 100%;
}

body {
    /* Body will now inherit background and color from html */
    font-family: "Optimistic";
    height: 100%;
    position: relative; /* Needed for animation container */
    overflow-x: hidden; /* Prevent horizontal scroll from animation */
    /* Prevent browser pull-to-refresh */
    overscroll-behavior-y: contain;
    -webkit-overflow-scrolling: touch;
    max-width: 100vw;
}

/* ---------------- Animated Background ---------------- */
.animated-background {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
    background: #f8f9fc;
    /* Solid enhanced background - no gradient as requested */
    overflow: hidden;
}

/* Gradient overlays removed to comply with no-gradient requirement */

/* All gradient overlays removed to comply with no-gradient requirement */

/* Animation keyframes removed with gradient overlays */

/* ---------------- Header ---------------- */
header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: var(--header-height);
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #ffffff;
    color: #1c1c1e;
    border-bottom: 1px solid #e0e0e0;
    z-index: 1000;
    padding: 0 var(--mobile-padding);
}

.app-name {
    font-size: 25px;
    font-weight: bold;
    color: #0e639c;
    text-align: center;
    width: 100%;
    margin: 0;
}

/* ---------------- Main ---------------- */
main {
    position: relative;
    margin-top: var(--header-height);
    padding: 20px var(--mobile-padding) 20px var(--mobile-padding);
    padding-bottom: calc(var(--nav-height) + 20px);
    min-height: calc(100vh - var(--header-height) - var(--nav-height));
    overflow-y: auto;
    overflow-x: hidden;
    margin-left: auto;
    margin-right: auto;
    max-width: var(--max-width);
    width: 100%;
    /* Additional overscroll prevention for main content */
    overscroll-behavior-y: contain;
    /* Prevent scroll sync between pages */
    scroll-behavior: auto;
    isolation: isolate;
}

/* ---------------- Page Transitions ---------------- */
.page-fade-out {
    animation: fadeOut 0.2s ease-out forwards;
}

.page-fade-in {
    animation: fadeIn 0.3s ease-in forwards;
}

@keyframes fadeOut {
    from {
        opacity: 1;
        transform: translateY(0);
    }

    to {
        opacity: 0;
        transform: translateY(10px);
    }
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* ---------------- Bottom Navigation ---------------- */
.bottom-nav {
    position: fixed;
    bottom: 0;
    width: 100%;
    background-color: #ffffff;
    display: flex;
    justify-content: space-around;
    align-items: center;
    padding: 8px 0;
    z-index: 1000;
    height: var(--nav-height);
}

.bottom-nav button {
    background: none;
    border: none;
    color: #555;
    font-size: 10px;
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer;
}

.bottom-nav .icon {
    width: 22px;
    height: 22px;
    margin-bottom: 2px;
    background-color: #555;
    /* Default inactive color */
    -webkit-mask-size: contain;
    mask-size: contain;
    -webkit-mask-position: center;
    mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-repeat: no-repeat;
    transition: background-color 0.3s ease;
}

.icon-home {
    -webkit-mask-image: url(./Assets/Icons/house.svg);
    mask-image: url(./Assets/Icons/house.svg);
}

.icon-history {
    -webkit-mask-image: url(./Assets/Icons/bar-chart.svg);
    mask-image: url(./Assets/Icons/bar-chart.svg);
}

.icon-subscriptions {
    -webkit-mask-image: url(./Assets/Icons/gem.svg);
    mask-image: url(./Assets/Icons/gem.svg);
}

.icon-profile {
    -webkit-mask-image: url(./Assets/Icons/user.svg);
    mask-image: url(./Assets/Icons/user.svg);
}

.icon-insights {
    -webkit-mask-image: url(./Assets/Icons/insights.svg);
    /* You need to add this icon */
    mask-image: url(./Assets/Icons/insights.svg);
}

.icon-edit {
    -webkit-mask-image: url(./Assets/Icons/edit.svg);
    mask-image: url(./Assets/Icons/edit.svg);
}

/* Hide tier-gated nav buttons by default to prevent flash of content */
.bottom-nav button[data-tier] {
    display: none;
}

.bottom-nav button.active {
    color: #0e639c;
}

.bottom-nav button.active .icon {
    background-color: #0e639c;
    /* Active color */
}

.bottom-nav button:hover .icon {
    animation: shake 0.3s ease-in-out;
}

@keyframes shake {
    0% {
        transform: translateX(0);
    }

    25% {
        transform: translateX(-3px);
    }

    50% {
        transform: translateX(3px);
    }

    75% {
        transform: translateX(-3px);
    }

    100% {
        transform: translateX(0);
    }
}

/* ---------------- Page Titles ---------------- */
.page-title {
    font-size: 20px;
    margin-bottom: 12px;
    font-weight: bold;
    color: #0e639c;
    text-align: center;
}

/* ---------------- Search Bar ---------------- */
.search-container {
    margin-bottom: 16px;
}

.search-wrapper {
    position: relative;
    background-color: #fff;
    border-radius: 8px;
    border: 1px solid #ccc;
    transition: border-color 0.3s ease;
}

.search-wrapper:focus-within {
    border-color: #0e639c;
}

#predictionSearch {
    width: 100%;
    padding: 10px 15px;
    border: none;
    background-color: transparent;
    color: #333;
    font-size: 14px;
    position: relative;
    z-index: 2;
}

#predictionSearch:focus {
    outline: none;
}

#search-ghost-text {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    padding: 10px 15px;
    font-size: 14px;
    color: #ccc;
    z-index: 1;
    pointer-events: none;
    white-space: nowrap;
    overflow: hidden;
}

#predictionSearch {
    padding-right: 35px; /* Make space for clear button */
}

#search-clear-btn {
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    font-size: 22px;
    color: #999;
    cursor: pointer;
    padding: 0 5px;
    line-height: 1;
    display: none; /* JS will toggle this */
}

/* ---------------- Cards ---------------- */
.card {
    background: #ffffff;
    border-radius: 10px;
    padding: 16px;
    margin-bottom: 16px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.08);
}

/* ---------------- Subscription Cards ---------------- */
.pricing-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 24px;
    margin-bottom: 20px;
}

/* ================== RESPONSIVE DESIGN ================== */

/* Small Mobile (up to 480px) */
@media (max-width: 480px) {
    :root {
        --mobile-padding: 12px;
    }

    .app-name {
        font-size: 20px;
    }

    main {
        padding: 15px var(--mobile-padding) 15px var(--mobile-padding);
        padding-bottom: calc(var(--nav-height) + 15px);
    }

    .bottom-nav button {
        font-size: 9px;
        padding: 4px 2px;
    }

    .bottom-nav .icon {
        width: 18px;
        height: 18px;
    }

    .subscription-card,
    .profile-card,
    .card {
        padding: 15px;
        margin-bottom: 15px;
    }

    .page-title {
        font-size: 1.5rem;
        margin-bottom: 15px;
    }
}

/* Large Mobile/Small Tablet (481px to 767px) */
@media (min-width: 481px) and (max-width: 767px) {
    :root {
        --mobile-padding: 16px;
    }

    .app-name {
        font-size: 22px;
    }

    main {
        padding: 18px var(--mobile-padding) 18px var(--mobile-padding);
        padding-bottom: calc(var(--nav-height) + 18px);
    }

    .subscription-card,
    .profile-card,
    .card {
        padding: 18px;
        margin-bottom: 18px;
    }
}

/* Tablet (768px to 1023px) */
@media (min-width: 768px) and (max-width: 1023px) {
    :root {
        --container-padding: 24px;
    }

    main {
        padding: 24px var(--container-padding) 24px var(--container-padding);
        padding-bottom: calc(var(--nav-height) + 24px);
    }

    .app-name {
        font-size: 26px;
    }

    .pricing-container {
        grid-template-columns: 1fr 1fr;
    }

    .subscription-card,
    .profile-card,
    .card {
        padding: 24px;
        margin-bottom: 24px;
    }

    .page-title {
        font-size: 2rem;
    }

    .bottom-nav button {
        font-size: 11px;
        padding: 6px 8px;
    }

    .bottom-nav .icon {
        width: 24px;
        height: 24px;
    }
}

/* Desktop (1024px and up) */
@media (min-width: 1024px) {
    :root {
        --container-padding: 32px;
    }

    main {
        padding: 32px var(--container-padding) 32px var(--container-padding);
        padding-bottom: calc(var(--nav-height) + 32px);
    }

    .app-name {
        font-size: 28px;
    }

    .pricing-container {
        grid-template-columns: 1fr 1fr;
        gap: 32px;
    }

    .subscription-card,
    .profile-card,
    .card {
        padding: 28px;
        margin-bottom: 28px;
    }

    .page-title {
        font-size: 2.2rem;
        margin-bottom: 24px;
    }

    .bottom-nav button {
        font-size: 12px;
        padding: 8px 12px;
    }

    .bottom-nav .icon {
        width: 26px;
        height: 26px;
    }

    /* Better spacing for large screens */
    .profile-container,
    .predictions-container {
        max-width: 900px;
        margin: 0 auto;
    }
}

/* Extra Large Desktop (1200px and up) */
@media (min-width: 1200px) {
    main {
        max-width: 1100px;
    }

    .pricing-container {
        grid-template-columns: repeat(2, 1fr);
        gap: 40px;
        max-width: 800px;
        margin: 0 auto;
    }

    .profile-container,
    .predictions-container {
        max-width: 1000px;
    }
}

/* ================== RESPONSIVE COMPONENTS ================== */

/* Responsive Buttons */
@media (max-width: 480px) {
    .button,
    .subscribe-btn,
    .btn-primary,
    .btn-secondary {
        padding: 10px 16px;
        font-size: 14px;
    }

    .modal-actions button {
        padding: 8px 12px;
        font-size: 13px;
    }
}

