.slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #e0e6ed;
    transition: 0.4s;
    border-radius: 34px;
    border: 2px solid #d1d9e0;
}

.slider::before {
    position: absolute;
    content: "";
    height: 18px;
    width: 18px;
    left: 3px;
    bottom: 3px;
    background-color: white;
    transition: 0.4s;
    border-radius: 50%;
}

input:checked+.slider {
    background-color: #0e639c;
}

input:checked+.slider::before {
    transform: translateX(26px);
}



