}

/* ---------------- Tabs ---------------- */
/* League tabs wrapper */
.league-tabs-wrapper {
    position: relative;
    margin-bottom: 20px;
    background: #ffffff;
    border-radius: 12px;
    padding: 12px 16px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    border: 1px solid rgba(0, 0, 0, 0.1);
    max-height: 80px;
    transition: max-height 0.3s ease, opacity 0.3s ease, transform 0.3s ease;
    overflow: hidden;
}

/* Scrollable tabs container */
.scrollable-tabs {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    scroll-behavior: smooth;
    scrollbar-width: none; /* Firefox */
    -ms-overflow-style: none; /* IE and Edge */
    padding: 0;
    margin-bottom: 0;
    flex-wrap: nowrap;
    scroll-snap-type: x proximity;
}

.scrollable-tabs::-webkit-scrollbar {
    display: none; /* Chrome, Safari, Opera */
}

/* Regular tab container for other pages */
.tab-container:not(.scrollable-tabs),
.history-tabs {
    display: flex;
    justify-content: center;
    gap: 12px;
    margin-bottom: 16px;
}

/* Remove collapsed state - tabs should always be visible on mobile */

.tab-btn {
    padding: 6px 16px;
    border-radius: 20px;
    border: 1px solid #0e639c;
    background: transparent;
    color: #0e639c;
    font-weight: 500;
    font-size: 14px;
    cursor: pointer;
    transition: all 0.3s ease;
    white-space: nowrap;
    flex-shrink: 0;
    scroll-snap-align: start;
}

.tab-btn.active {
    background: #0e639c;
    color: #fff;
    border-color: #0e639c;
}

.tab-btn:hover {
    background: #0e639c;
    color: #fff;
    border-color: #0e639c;
    transform: translateY(-1px);
    box-shadow: 0 2px 4px rgba(14, 99, 156, 0.2);
}

/* Dark mode support for league tabs */
.dark-mode .league-tabs-wrapper {
    background: #2d2d30;
    border-color: rgba(255, 255, 255, 0.1);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
}

.dark-mode .tab-btn {
    color: #d4d4d4;
    border-color: #555;
}

.dark-mode .tab-btn.active {
    background: #0e639c;
    color: #fff;
    border-color: #0e639c;
}

.dark-mode .tab-btn:hover {
    background: #0e639c;
    color: #fff;
    border-color: #0e639c;
}

/* Show/Hide containers */
.pricing-container,
.tab-content {
    display: none;
    /* hidden by default */
}

.pricing-container.active,
.tab-content.active {
    display: grid; /* Use grid for active pricing container */
}

.tab-content.active {
    display: block; /* Keep block for other tab content */
}

/* HomePage */
.predictions-container {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.history-card:hover,
.prediction-card:hover {
    transition: transform 0.2s ease;
    transform: translateY(-4px);
}

/* Match Title */
.match-title {
    font-size: 1.2rem;
    margin-bottom: 0.4rem;
    color: #111;
}

/* Prediction Details */
.prediction-detail {
    font-size: 1rem;
    color: #444;
}

.odds {
    font-size: 0.95rem;
    margin: 0.2rem 0;
    color: #e8590c;
    /* Adjusted for light theme contrast */
}

.match-time {
    font-size: 0.9rem;
    color: #777;
    margin-bottom: 0.8rem;
}

/* Confidence Section */
.confidence {
    margin-top: 0.6rem;
}

.confidence span {
    display: block;
    margin-bottom: 0.4rem;
    font-size: 0.9rem;
    color: #555;
}

.confidence-bar {
    width: 100%;
    height: 8px;
    background: #e9ecef;
    border-radius: 5px;
    overflow: hidden;
    margin-top: 5px;
}

.confidence-fill {
    height: 100%;
}

/* High, Medium, Low confidence borders are fine as they are */

/* High Confidence */
.prediction-card.high-confidence {
    border-color: #4caf50;
    --tier-color: #4caf50;
}

.prediction-card.high-confidence .confidence-fill {
    background: linear-gradient(90deg, #388e3c, #4caf50);
}

/* Medium Confidence */
.prediction-card.medium-confidence {
    border-color: #ff9800;
    --tier-color: #ff9800;
}

.prediction-card.medium-confidence .confidence-fill {
    background: linear-gradient(90deg, #f57c00, #ff9800);
}

/* Low Confidence */
.prediction-card.low-confidence {
    border-color: #f44336;
    --tier-color: #f44336;
}

.prediction-card.low-confidence .confidence-fill {
    background: linear-gradient(90deg, #d32f2f, #f44336);
}

/* AI Pick Styling */
.ai-pick {
    border: 2px solid var(--tier-color);
    padding: 4px 8px;
    border-radius: 6px;
    font-weight: bold;
    display: inline-block;
    color: var(--tier-color);
    /* optional: makes text match tier */
}

.card-animation {
    animation: card-enter 0.4s ease-out forwards;
}

@keyframes card-enter {
    from {
        opacity: 0;
        transform: translateY(15px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.dark-mode #search-clear-btn:hover {
    color: #eee;
}

/* ---------------- Active Filters ---------------- */
#active-filters-container {
    display: flex;
    flex-wrap: wrap;
    gap: 8px;
    margin-bottom: 16px;
    justify-content: center;
}

.filter-pill {
    background-color: #e7f5ff;
    color: #0e639c;
    padding: 5px 10px;
    border-radius: 16px;
    font-size: 13px;
    font-weight: 500;
    display: flex;
    align-items: center;
    gap: 6px;
    cursor: pointer;
    transition: background-color 0.2s ease;
}

.filter-pill:hover {
    background-color: #d0eaff;
}

.filter-pill .close-icon {
    font-weight: bold;
    font-size: 14px;
}

.no-results-message {
    text-align: center;
    color: #777;
    padding: 20px;
    display: none;
    /* Hidden by default */
}

/* ---------------- Pull to Refresh ---------------- */
#refresh-indicator {
    position: fixed;
    top: calc(var(--header-height) + 15px);
    left: 50%;
    transform: translateX(-50%) scale(0);
    width: 60px;
    height: 60px;
    background: #0e639c;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: 0 8px 25px rgba(14, 99, 156, 0.4);
    border: 3px solid rgba(255, 255, 255, 0.3);
    opacity: 0;
    z-index: 1000;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    will-change: transform, opacity;
    touch-action: none;
}

#refresh-indicator .refresh-icon {
    width: 40px;
    height: 40px;
    filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.2));
}

#refresh-indicator.transitioning {
    transition: transform 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94), 
                opacity 0.4s ease, 
                box-shadow 0.4s ease,
                background 0.4s ease;
}

#refresh-indicator.refreshing {
    animation: enhanced-pulse 1.5s ease-in-out infinite, 
               ripple 2s ease-out infinite;
    background: #4caf50;
    box-shadow: 0 12px 35px rgba(76, 175, 80, 0.5);
    border-color: rgba(76, 175, 80, 0.5);
}

#refresh-indicator.refreshing .refresh-icon {
    animation: enhanced-spin 1.2s cubic-bezier(0.68, -0.55, 0.265, 1.55) infinite;
    transform: scale(1.2);
    filter: drop-shadow(0 2px 8px rgba(76, 175, 80, 0.6)) brightness(1.1);
}

@keyframes enhanced-pulse {
    0%, 100% { 
        transform: translateX(-50%) scale(1); 
        box-shadow: 0 8px 25px rgba(14, 99, 156, 0.4), 0 0 0 0 rgba(14, 99, 156, 0.1);
    }
    50% { 
        transform: translateX(-50%) scale(1.08); 
        box-shadow: 0 15px 40px rgba(76, 175, 80, 0.6), 0 0 0 20px rgba(14, 99, 156, 0.05);
    }
}

@keyframes enhanced-spin {
    0% { 
        transform: scale(1.2) rotate(0deg);
        filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3));
    }
    25% {
        transform: scale(1.3) rotate(90deg);
        filter: drop-shadow(0 3px 6px rgba(0, 0, 0, 0.4));
    }
    50% {
        transform: scale(1.25) rotate(180deg);
        filter: drop-shadow(0 4px 8px rgba(0, 0, 0, 0.5));
    }
    75% {
        transform: scale(1.3) rotate(270deg);
        filter: drop-shadow(0 3px 6px rgba(0, 0, 0, 0.4));
    }
    100% { 
        transform: scale(1.2) rotate(360deg);
        filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3));
    }
}

@keyframes ripple {
    0% {
        box-shadow: 0 12px 35px rgba(76, 175, 80, 0.5), 0 0 0 0 rgba(76, 175, 80, 0.4);
    }
    50% {
        box-shadow: 0 15px 40px rgba(76, 175, 80, 0.6), 0 0 0 30px rgba(76, 175, 80, 0.1);
    }
    100% {
        box-shadow: 0 12px 35px rgba(76, 175, 80, 0.5), 0 0 0 50px rgba(76, 175, 80, 0);
    }
}

/* Mobile-specific optimizations for pull-to-refresh */
main[data-pull-to-refresh-active="true"] {
    overscroll-behavior-y: contain;
    -webkit-overflow-scrolling: touch;
    will-change: transform;
}

/* Disable text selection during pull-to-refresh */
body.pulling-to-refresh {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    overflow: hidden;
}

/* Ensure smooth scrolling on iOS */
@supports (-webkit-appearance: none) {
    main[data-pull-to-refresh-active="true"] {
        -webkit-overflow-scrolling: touch;
        -webkit-transform: translate3d(0, 0, 0);
    }
}

