    display: flex;
    justify-content: center;
    gap: 10px;
    margin-bottom: 30px;
}

.indicator {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    animation: wave 2s infinite;
}

.indicator.active {
    background: white;
}

.indicator:nth-child(2) {
    animation-delay: 0.3s;
}

.indicator:nth-child(3) {
    animation-delay: 0.6s;
}

.welcome-back-message {
    margin-bottom: 20px;
    padding: 20px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    backdrop-filter: blur(10px);
    animation: slideIn 0.5s ease;
}

.welcome-back-message h2 {
    font-size: 1.5rem;
    margin-bottom: 10px;
}

.inactive-stats {
    display: flex;
    justify-content: space-around;
    margin-bottom: 30px;
    gap: 20px;
}

.stat-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 15px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 10px;
    backdrop-filter: blur(5px);
    flex: 1;
}

.stat-number {
    font-size: 1.8rem;
    font-weight: bold;
    margin-bottom: 5px;
}

.stat-label {
    font-size: 0.9rem;
    opacity: 0.8;
}

.return-btn {
    background: white;
    color: #0e639c;
    border: none;
    padding: 12px 30px;
    border-radius: 25px;
    font-size: 1rem;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.return-btn:hover {
    background: #f8f9fa;
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.15);
}

/* Animations */
@keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
}

@keyframes wave {
    0%, 100% { transform: scale(1); opacity: 1; }
    50% { transform: scale(1.2); opacity: 0.7; }
}

@keyframes slideIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

/* Dark mode support */
.dark-mode .inactive-container {
    background: linear-gradient(135deg, #1e1e1e 0%, #2d2d30 100%);
}

.dark-mode .logo-icon {
    background: rgba(165, 216, 255, 0.2);
}

.dark-mode .return-btn {
    background: #2d2d30;
    color: #a5d8ff;
}

.dark-mode .return-btn:hover {
    background: #3d3d40;
}

/* Mobile responsiveness */
@media (max-width: 480px) {
    .inactive-content {
        padding: 20px 15px;
    }

    .inactive-title {
        font-size: 1.5rem;
    }

    .inactive-message {
        font-size: 1rem;
    }

    .inactive-stats {
        flex-direction: column;
        gap: 15px;
    }

    .stat-item {
        flex-direction: row;
        justify-content: space-between;
        text-align: left;
    }
}

/* ===== AdBlocker Overlay Styles ===== */
#adblocker-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.95);
    z-index: 10000;
    display: flex;
    align-items: center;
    justify-content: center;
    backdrop-filter: blur(10px);
}

.adblocker-container {
    background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
    border-radius: 20px;
    padding: 40px;
    max-width: 500px;
    width: 90%;
    text-align: center;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
    border: 1px solid rgba(76, 175, 80, 0.2);
}

.adblocker-content h2 {
    color: #ffffff;
    margin-bottom: 20px;
    font-size: 28px;
    font-weight: 600;
}

.adblocker-icon {
    font-size: 64px;
    margin-bottom: 20px;
}

.adblocker-content p {
    color: #b0b0b0;
    margin-bottom: 20px;
    line-height: 1.6;
}

.adblocker-content ul {
    text-align: left;
    color: #ffffff;
    margin: 20px 0;
    padding-left: 0;
    list-style: none;
}

.adblocker-content li {
    margin: 10px 0;
    padding: 12px 20px;
    background: rgba(76, 175, 80, 0.1);
    border-radius: 10px;
    border-left: 4px solid #4caf50;
}

.adblocker-buttons {
    margin: 30px 0 20px;
    display: flex;
    gap: 15px;
    flex-wrap: wrap;
    justify-content: center;
}

.btn-refresh, .btn-upgrade {
    padding: 12px 24px;
    border: none;
    border-radius: 10px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    flex: 1;
    min-width: 140px;
}

.btn-refresh {
    background: linear-gradient(135deg, #4caf50 0%, #45a049 100%);
    color: white;
}

.btn-refresh:hover {
    background: linear-gradient(135deg, #45a049 0%, #3d8b40 100%);
    transform: translateY(-2px);
}

.btn-upgrade {
    background: linear-gradient(135deg, #ff9800 0%, #f57c00 100%);
    color: white;
}

.btn-upgrade:hover {
    background: linear-gradient(135deg, #f57c00 0%, #ef6c00 100%);
    transform: translateY(-2px);
}

.adblocker-note {
    color: #888;
    font-size: 14px;
    font-style: italic;
    margin-top: 20px;
}

@media (max-width: 480px) {
    .adblocker-container {
        padding: 30px 20px;
        margin: 20px;
    }

    .adblocker-buttons {
        flex-direction: column;
    }

    .btn-refresh, .btn-upgrade {
        width: 100%;
    }
}

