/* /Auth/auth.css */

/* --- Global & Font --- */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

@font-face {
    font-family: 'Optimistic';
    src: url('../Assets/Fonts/Optimistic_Text_A_Md.ttf');
}

/* --- Body & Theme --- */
body {
    font-family: "Optimistic", sans-serif;
    background: #f8f9fc;
    color: #1a1a1c;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    padding: 20px;
    position: relative; /* For background animation positioning */
    overflow: hidden; /* Hide animation overflow */
}

/* --- Auth Container & Card --- */
.auth-container {
    width: 100%;
    max-width: 420px;
}

.auth-card {
    background: #ffffff;
    border-radius: 16px;
    padding: 32px;    
    border: 2px solid #0e639c;
    box-shadow: 0 4px 12px rgba(14, 99, 156, 0.12); /* Enhanced shadow with blue tint */
    position: relative; /* For theme toggle positioning */
    text-align: center;
}

/* Theme Toggle Button */
.theme-toggle {
    position: absolute;
    top: 16px;
    right: 16px;
    background: none;
    border: 2px solid #e0e0e0;
    border-radius: 50%;
    width: 40px;
    height: 40px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    z-index: 1000;
}

.theme-toggle:hover {
    border-color: #0e639c;
    transform: scale(1.1);
}

.theme-icon {
    font-size: 18px;
    transition: transform 0.3s ease;
}

.theme-toggle:hover .theme-icon {
    transform: rotate(180deg);
}

/* Theme Transition Circle */
.theme-transition-circle {
    position: fixed;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    border-radius: 50%;
    background: #1e1e1e;
    transform: translate(-50%, -50%);
    transition: all 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    z-index: 999;
    pointer-events: none;
    opacity: 0;
}

.theme-transition-circle.expanding {
    width: 200vmax;
    height: 200vmax;
    opacity: 1;
}

.theme-transition-circle.light-mode {
    background: #f8f9fc;
}

.auth-card {
    transition: opacity 0.5s ease-out, transform 0.5s ease-out;
}
.auth-card.fade-out {
    opacity: 0;
    transform: translateY(-20px);
}

.auth-title {
    font-size: 32px;
    font-weight: bold;
    color: #0e639c;
    margin-bottom: 8px;
}

.auth-subtitle {
    font-size: 16px;
    color: #555;
    margin-bottom: 24px;
}

