// utils.js
import { supabase } from './env.js';

/**
 * Fetches the user's public IP address.
 * @returns {Promise<string>} The public IP or "Unknown".
 */
export async function getPublicIP() {
    try {
        const res = await fetch("https://api.ipify.org?format=json");
        if (!res.ok) return "Unknown";
        const data = await res.json();
        return data.ip || "Unknown";
    } catch {
        return "Unknown";
    }
}

/**
 * Formats a timestamp string or Date into a "DD/MM/YYYY HH:mm" string.
 * @param {string|Date} timestamp - The timestamp to format.
 * @returns {string} The formatted date string or an empty string.
 */
export function formatTimestamp(timestamp) {
    try {
        if (!timestamp) return "";

        let date;
        if (typeof timestamp === 'string') {
            date = new Date(timestamp);
        } else if (timestamp instanceof Date) {
            date = timestamp;
        } else if (timestamp?.toDate) {
            // Firestore Timestamp object
            date = timestamp.toDate();
        } else {
            return "";
        }

        if (isNaN(date.getTime())) return "";

        const day = String(date.getDate()).padStart(2, "0");
        const month = String(date.getMonth() + 1).padStart(2, "0");
        const year = date.getFullYear();
        const hours = String(date.getHours()).padStart(2, "0");
        const mins = String(date.getMinutes()).padStart(2, "0");
        return `${day}/${month}/${year} ${hours}:${mins}`;
    } catch (error) {
        console.warn('Error formatting timestamp:', error);
        return "";
    }
}

/**
 * Adds a unique action to the user's history log via Supabase.
 * @param {string} userId - The user's ID.
 * @param {string} action - The action description to log.
 */
export async function addHistoryUnique(userId, action) {
    if (!userId || !action) return;

    try {
        // Get the user's last history entry to prevent duplicates
        const { data: lastEntry, error: fetchError } = await supabase
            .from('user_history')
            .select('action')
            .eq('user_id', userId)
            .order('created_at', { ascending: false })
            .limit(1)
            .single();

        // Prevent logging the exact same action back-to-back
        if (lastEntry && lastEntry.action === action) return;

        const ip = await getPublicIP();
        const { error } = await supabase
            .from('user_history')
            .insert({
                user_id: userId,
                action: action,
                ip_address: ip,
                created_at: new Date().toISOString()
            });

        if (error) {
            console.warn('Failed to add history:', error);
        }
    } catch (err) {
        console.error("Failed to add history:", err);
    }
}

styles.css.content:
* {
    margin: 0;
    padding: 0;
    text-decoration: none;
    list-style-type: none;
    font-family: "Optimistic";
    box-sizing: border-box;
}

::-webkit-scrollbar {
    display: none;
}

@font-face {
    font-family: 'Optimistic';
    src: url('./Assets/Fonts/Optimistic_Text_A_Md.ttf');
}

:root {
    --header-height: 60px;
    --nav-height: 60px;
    --max-width: 1200px;
    --container-padding: 20px;
    --mobile-padding: 15px;
}

/* ---------------- Enhanced Visual Variables ---------------- */
:root {
    --border-width-thin: 2px;
    --border-width-medium: 3px;
    --border-width-thick: 4px;
    --border-radius-small: 8px;
    --border-radius-medium: 12px;
    --border-radius-large: 16px;
    --shadow-light: 0 2px 8px rgba(14, 99, 156, 0.08);
    --shadow-medium: 0 4px 12px rgba(14, 99, 156, 0.12);
    --shadow-strong: 0 6px 20px rgba(14, 99, 156, 0.16);
}

/* ---------------- Body ---------------- */
html {
    background: #f8f9fc;
    /* Enhanced light background - more vibrant than dull #f4f5f7 */
    color: #1a1a1c;
    /* Slightly deeper text color for better contrast */
    font-family: "Optimistic";
    height: 100%;
}

body {
    /* Body will now inherit background and color from html */
    font-family: "Optimistic";
    height: 100%;
    position: relative; /* Needed for animation container */
    overflow-x: hidden; /* Prevent horizontal scroll from animation */
    overflow-y: auto; /* Ensure vertical scrolling works */
    /* Better scroll behavior for all devices */
    -webkit-overflow-scrolling: touch;
    max-width: 100vw;
}

/* ---------------- Animated Background ---------------- */
.animated-background {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
    background: #f8f9fc;
    /* Solid enhanced background - no gradient as requested */
    overflow: hidden;
}

/* Gradient overlays removed to comply with no-gradient requirement */

/* All gradient overlays removed to comply with no-gradient requirement */

/* Animation keyframes removed with gradient overlays */

/* ---------------- Header ---------------- */
header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: var(--header-height);
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #ffffff;
    color: #1c1c1e;
    border-bottom: 3px solid #0e639c;
    box-shadow: 0 2px 8px rgba(14, 99, 156, 0.1);
    z-index: 1000;
    padding: 0 var(--mobile-padding);
}

.app-name {
    font-size: 25px;
    font-weight: bold;
    color: #0e639c;
    text-align: center;
    width: 100%;
    margin: 0;
}

/* ---------------- Main ---------------- */
main {
    position: relative;
    margin-top: var(--header-height);
    padding: 20px var(--mobile-padding) 20px var(--mobile-padding);
    padding-bottom: calc(var(--nav-height) + 20px);
    min-height: calc(100vh - var(--header-height) - var(--nav-height));
    overflow-y: auto;
    overflow-x: hidden;
    margin-left: auto;
    margin-right: auto;
    max-width: var(--max-width);
    width: 100%;
    /* Better scroll behavior for desktop */
    -webkit-overflow-scrolling: touch;
    overscroll-behavior-y: auto;
    scroll-behavior: smooth;
}

/* ---------------- Page Transitions ---------------- */
.page-fade-out {
    animation: fadeOut 0.2s ease-out forwards;
}

.page-fade-in {
    animation: fadeIn 0.3s ease-in forwards;
}

@keyframes fadeOut {
    from {
        opacity: 1;
        transform: translateY(0);
    }

    to {
        opacity: 0;
        transform: translateY(10px);
    }
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* ---------------- Bottom Navigation ---------------- */
.bottom-nav {
    position: fixed;
    bottom: 0;
    width: 100%;
    background-color: #ffffff;
    display: flex;
    justify-content: space-around;
    align-items: center;
    padding: 8px 0;
    z-index: 1000;
    height: var(--nav-height);
}

.bottom-nav button {
    background: none;
    border: none;
    color: #555;
    font-size: 10px;
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer;
}

.bottom-nav .icon {
    width: 22px;
    height: 22px;
    margin-bottom: 2px;
    background-color: #555;
    /* Default inactive color */
    -webkit-mask-size: contain;
    mask-size: contain;
    -webkit-mask-position: center;
    mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-repeat: no-repeat;
    transition: background-color 0.3s ease;
}

.icon-home {
    -webkit-mask-image: url(./Assets/Icons/house.svg);
    mask-image: url(./Assets/Icons/house.svg);
}

.icon-history {
    -webkit-mask-image: url(./Assets/Icons/bar-chart.svg);
    mask-image: url(./Assets/Icons/bar-chart.svg);
}

.icon-subscriptions {
    -webkit-mask-image: url(./Assets/Icons/gem.svg);
    mask-image: url(./Assets/Icons/gem.svg);
}

.icon-profile {
    -webkit-mask-image: url(./Assets/Icons/user.svg);
    mask-image: url(./Assets/Icons/user.svg);
}

.icon-insights {
    -webkit-mask-image: url(./Assets/Icons/insights.svg);
    /* You need to add this icon */
    mask-image: url(./Assets/Icons/insights.svg);
}

.icon-edit {
    -webkit-mask-image: url(./Assets/Icons/edit.svg);
    mask-image: url(./Assets/Icons/edit.svg);
}

/* Hide tier-gated nav buttons by default to prevent flash of content */
.bottom-nav button[data-tier] {
    display: none;
}

.bottom-nav button.active {
    color: #0e639c;
}

.bottom-nav button.active .icon {
    background-color: #0e639c;
    /* Active color */
}

.bottom-nav button:hover .icon {
    animation: shake 0.3s ease-in-out;
}

@keyframes shake {
    0% {
        transform: translateX(0);
    }

    25% {
        transform: translateX(-3px);
    }

    50% {
        transform: translateX(3px);
    }

    75% {
        transform: translateX(-3px);
    }

    100% {
        transform: translateX(0);
    }
}

/* ---------------- Page Titles ---------------- */
.page-title {
    font-size: 20px;
    margin-bottom: 12px;
    font-weight: bold;
    color: #0e639c;
    text-align: center;
}

/* ---------------- Search Bar ---------------- */
.search-container {
    margin-bottom: 16px;
}

.search-wrapper {
    position: relative;
    background-color: #fff;
    border-radius: 8px;
    border: 1px solid #ccc;
    transition: border-color 0.3s ease;
}

.search-wrapper:focus-within {
    border-color: #0e639c;
}

#predictionSearch {
    width: 100%;
    padding: 10px 15px;
    border: none;
    background-color: transparent;
    color: #333;
    font-size: 14px;
    position: relative;
    z-index: 2;
}

#predictionSearch:focus {
    outline: none;
}

#search-ghost-text {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    padding: 10px 15px;
    font-size: 14px;
    color: #ccc;
    z-index: 1;
    pointer-events: none;
    white-space: nowrap;
    overflow: hidden;
}

#predictionSearch {
    padding-right: 35px; /* Make space for clear button */
}

#search-clear-btn {
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    font-size: 22px;
    color: #999;
    cursor: pointer;
    padding: 0 5px;
    line-height: 1;
    display: none; /* JS will toggle this */
}

/* ---------------- Cards ---------------- */
.card {
    background: #ffffff;
    border-radius: 10px;
    padding: 16px;
    margin-bottom: 16px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.08);
}

/* ---------------- Subscription Cards ---------------- */
.pricing-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 24px;
    margin-bottom: 20px;
}

/* ================== RESPONSIVE DESIGN ================== */

/* Small Mobile (up to 480px) */
@media (max-width: 480px) {
    :root {
        --mobile-padding: 12px;
    }

    .app-name {
        font-size: 20px;
    }

    main {
        padding: 15px var(--mobile-padding) 15px var(--mobile-padding);
        padding-bottom: calc(var(--nav-height) + 15px);
    }

    .bottom-nav button {
        font-size: 9px;
        padding: 4px 2px;
    }

    .bottom-nav .icon {
        width: 18px;
        height: 18px;
    }

    .subscription-card,
    .profile-card,
    .card {
        padding: 15px;
        margin-bottom: 15px;
    }

