/* Enhanced SVG Toggle Switch */
:root {
    --toggle-off: #b8c0c7;
    --toggle-track: #b8c0c7;
    --toggle-ring: #b8c0c7;
    --toggle-stroke-off: #ffffff;
    --toggle-stroke-on: #ffffff;
}

.dark-mode {
    --toggle-off: #5b6270;
    --toggle-track: #5b6270;
    --toggle-ring: #5b6270;
    --toggle-stroke-off: #e6e6e6;
    --toggle-stroke-on: #ffffff;
}

.toggle-input {
    position: absolute;
    opacity: 0;
    height: 1px;
    width: 1px;
    pointer-events: none;
}

.switch {
    --a: 0.5s ease-out;
    cursor: pointer;
    position: relative;
    display: inline-flex;
    height: 17px;
    border-radius: 999px;
    box-shadow: 0 0 0 2px #000000;
    aspect-ratio: 212.4992/84.4688;
    background-color: var(--toggle-track);
    color: var(--toggle-stroke-off);
    transition: all var(--a);
}

.switch svg {
    height: 100%;
}

.switch svg path {
    color: currentColor;
    stroke-width: 16;
    stroke-linecap: round;
    stroke-linejoin: round;
    stroke-dasharray: 136 224;
    transition: all var(--a), 0s transform;
    transform-origin: center;
}

.toggle-input:checked + .switch {
    color: var(--toggle-stroke-on);
    background-color: #0e639c;
    box-shadow: 0 0 0 2px #000000;
}

.toggle-input:checked + .switch svg path {
    stroke-dashoffset: 180;
    transform: scaleY(-1);
}

.switch:focus-visible {
    outline: 3px solid var(--statwise-blue, #0e639c);
    outline-offset: 3px;
}

.setting-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 12px 0;
    gap: 12px;
}



