/* ---------------- Tabs ---------------- */
/* League tabs wrapper */
.league-tabs-wrapper {
    position: relative;
    margin-bottom: 20px;
    background: #ffffff;
    border-radius: 12px;
    padding: 12px 16px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    border: 1px solid rgba(0, 0, 0, 0.1);
    max-height: 80px;
    transition: max-height 0.3s ease, opacity 0.3s ease, transform 0.3s ease;
    overflow: hidden;
}

/* Scrollable tabs container */
.scrollable-tabs {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    scroll-behavior: smooth;
    scrollbar-width: none; /* Firefox */
    -ms-overflow-style: none; /* IE and Edge */
    padding: 0;
    margin-bottom: 0;
    flex-wrap: nowrap;
    scroll-snap-type: x proximity;
}

.scrollable-tabs::-webkit-scrollbar {
    display: none; /* Chrome, Safari, Opera */
}

/* Regular tab container for other pages */
.tab-container:not(.scrollable-tabs),
.history-tabs {
    display: flex;
    justify-content: center;
    gap: 12px;
    margin-bottom: 16px;
}

