}

.dark-mode .tier-expiry-date {
    color: #bbb;
}

.dark-mode .subscription-card ul li,
.dark-mode .profile-card p,
.dark-mode .profile-email {
    color: #bbb;
}

/* Dark mode support for new subscription elements */
.dark-mode .prediction-limit {
    background: rgba(14, 99, 156, 0.2);
    color: #74b9ff;
}

.dark-mode .feature-highlight {
    background: #1c3a50;
    color: #a5d8ff;
}

.dark-mode .success-rate {
    background: #1b4332;
    color: #52c41a;
}

.dark-mode .roi-indicator {
    background: #3d2914;
    color: #ffa940;
}

.dark-mode .guarantee {
    background: #2d1b3d;
    color: #b37feb;
    border-color: #b37feb;
}

.dark-mode .profile-username {
    color: #fff;
}

.dark-mode .modal-content {
    background: #1e1e1e;
    color: #fff;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
}

.dark-mode .history-card,
.dark-mode .prediction-card {
    background-color: #1e1e1e;
    border-color: #444;
    /* Default border color for cards in dark mode */
    color: #fff;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
}

.dark-mode .history-title,
.dark-mode .match-title {
    color: #fff;
}

.dark-mode .history-detail,
.dark-mode .prediction-detail {
    color: #ddd;
}

.dark-mode .history-time,
.dark-mode .match-time {
    color: #aaa;
}

.dark-mode .tab-btn {
    background: #2d2d30;
    color: #d4d4d4;
    border-color: #555;
}

.dark-mode .tab-btn.active {
    background: #0e639c;
    color: #fff;
    border-color: #0e639c;
}

.dark-mode .tab-btn:hover {
    background: #0b4f7a;
    color: #fff;
    border-color: #0b4f7a;
}

/* Dark mode toggle slider styles */
.dark-mode .slider {
    background-color: #444;
    border-color: #555;
}

.dark-mode input:checked+.slider {
    background-color: #0e639c;
    border-color: #0e639c;
    box-shadow: 0 0 10px rgba(14, 99, 156, 0.4);
}

/* Ensure confidence colors override the default dark mode border */
.dark-mode .prediction-card.high-confidence {
    border-color: #4caf50;
}

.dark-mode .prediction-card.medium-confidence {
    border-color: #ff9800;
}

.dark-mode .prediction-card.low-confidence {
    border-color: #f44336;
}


.dark-mode .odds {
    color: #ffb300;
}

.dark-mode .confidence span {
    color: #ccc;
}

.dark-mode .confidence-bar {
    background: #333;
}

.dark-mode .no-results-message {
    color: #aaa;
}

.dark-mode #refresh-indicator {
    background: #2d2d30;
    color: #d4d4d4;
    box-shadow: 0 3px 10px rgba(0, 0, 0, 0.4);
}

/* ---------------- Loader ---------------- */
#globalLoader {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 9999;
    display: none;
    /* Initially hidden by JS */
    justify-content: center;
    align-items: center;
}

.loader-overlay {
    position: absolute;
    width: 100%;
    height: 100%;
    background: rgba(244, 245, 247, 0.7);
    /* Light theme overlay, matches body bg */
}

.loader-spinner {
    width: 50px;
    height: 50px;
    border: 5px solid #e0e0e0;
    /* Light theme spinner track */
    border-top-color: #0e639c;
    /* Spinner color */
    border-radius: 50%;
    animation: spin 1s linear infinite;
    z-index: 1;
}

@keyframes spin {
    to {
        transform: rotate(360deg);
    }
}

.dark-mode .loader-overlay {
    background: rgba(30, 30, 30, 0.7);
    /* Dark theme overlay */
}

.dark-mode .loader-spinner {
    border-color: #444;
    /* Dark theme spinner track */
    border-top-color: #0e639c;
    /* Spinner color can stay the same */
}

.button {
    display: inline-block;
    padding: 8px 16px;
    border-radius: 8px;
    background: #0e639c;
    color: #fff;
    font-weight: bold;
    text-decoration: none;
    transition: background 0.3s ease;
    border: none;
    cursor: pointer;
    text-align: center;
}

.button:hover {
    background: #0b4f7a;
}

.button.btn-danger {
    background: #d9534f;
}

.button.btn-danger:hover {
    background: #c9302c;
}

