    color: #555;
    margin-bottom: 12px;
    line-height: 1.5;
}

.manage-card ul {
    list-style-type: '‚úî  ';
    padding-left: 20px;
    margin-top: 10px;
}

.manage-card ul li {
    margin-bottom: 5px;
    font-size: 14px;
    color: #444;
}

.manage-card.danger-zone {
