/* ---------------- Referral Page ---------------- */
.referral-table-wrapper {
    margin-top: 16px;
}

.table-responsive {
    width: 100%;
    overflow-x: auto;
    -webkit-overflow-scrolling: touch;
}

.referral-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 12px;
    background: var(--card-bg, #fff);
    border-radius: 8px;
    overflow: hidden;
}

.referral-table thead {
    background: var(--statwise-blue, #0e639c);
    color: white;
}

.referral-table th {
    padding: 12px 16px;
    text-align: left;
    font-weight: 600;
    font-size: 14px;
    white-space: nowrap;
}

.referral-table tbody tr {
    border-bottom: 1px solid var(--border-color, #e0e0e0);
    transition: background-color 0.2s ease;
}

.referral-table tbody tr:hover {
    background: rgba(14, 99, 156, 0.05);
}

.referral-table tbody tr:last-child {
    border-bottom: none;
}

.referral-table td {
    padding: 12px 16px;
    font-size: 14px;
    color: var(--text-primary, #333);
}

.tier-badge-small {
    display: inline-block;
    padding: 4px 8px;
    border-radius: 12px;
    font-size: 12px;
    font-weight: 500;
    background: rgba(14, 99, 156, 0.1);
    color: var(--statwise-blue, #0e639c);
}

.reward-status {
    display: inline-block;
    padding: 4px 8px;
    border-radius: 12px;
    font-size: 12px;
    font-weight: 500;
}

.reward-status.claimed {
    background: rgba(76, 175, 80, 0.1);
    color: #4caf50;
}

.reward-status.pending {
    background: rgba(255, 152, 0, 0.1);
    color: #ff9800;
}

.dark-mode .referral-table {
    background: var(--card-bg, #2d2d30);
}

.dark-mode .referral-table thead {
    background: rgba(14, 99, 156, 0.9);
}

.dark-mode .referral-table tbody tr {
    border-bottom-color: var(--border-color, #444);
}

.dark-mode .referral-table tbody tr:hover {
    background: rgba(14, 99, 156, 0.1);
}

.dark-mode .referral-table td {
    color: var(--text-primary, #e2e2e4);
}

.dark-mode .tier-badge-small {
    background: rgba(14, 99, 156, 0.2);
    color: #74b9ff;
}

/* Responsive table for mobile */
@media (max-width: 768px) {
    .referral-table thead {
        display: none;
    }

    .referral-table tbody tr {
        display: block;
        margin-bottom: 16px;
        border: 1px solid var(--border-color, #e0e0e0);
        border-radius: 8px;
        padding: 12px;
    }

    .referral-table td {
        display: flex;
        justify-content: space-between;
        padding: 8px 0;
        border: none;
    }

    .referral-table td:before {
        content: attr(data-label);
        font-weight: 600;
        color: var(--text-secondary, #666);
    }

    .dark-mode .referral-table td:before {
        color: var(--text-secondary, #bbb);
    }
}

.referral-code-container {
    display: flex;
    gap: 8px;
    margin-top: 8px;
}

#referralCodeInput {
    flex-grow: 1;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 8px;
    background-color: #f1f3f5;
    color: #333;
    font-size: 16px;
    font-weight: bold;
    text-align: center;
}

#referralCodeInput:focus {
    outline: none;
}

.dark-mode #referralCodeInput {
    background-color: #252526;
    border-color: #555;
    color: #d4d4d4;
}

.form-description {
    font-size: 14px;
    color: #666;
    margin-bottom: 16px;
    text-align: center;
    line-height: 1.5;
}

/* ---------------- Referral Page Share Buttons ---------------- */
.share-buttons-container {
    display: flex;
    justify-content: center;
    gap: 16px;
    margin-top: 20px;
    padding-top: 16px;
    border-top: 1px solid #e0e0e0;
}

.share-btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: 44px;
    height: 44px;
    border-radius: 50%;
    border: none;
    cursor: pointer;
    transition: transform 0.2s ease, background-color 0.2s ease;
    padding: 0;
    background-color: #f1f3f5;
}

.share-btn:hover {
    transform: scale(1.1);
}

.share-btn svg {
    width: 22px;
    height: 22px;
    color: #333;
}

.share-btn.whatsapp:hover {
    background-color: #e0f8e5;
}

.share-btn.whatsapp:hover svg {
    color: #25D366;
}

.share-btn.twitter:hover {
    background-color: #e1f5fe;
}

.share-btn.twitter:hover svg {
    color: #1DA1F2;
}

.share-btn.generic:hover {
    background-color: #e8eaf6;
}

.share-btn.generic:hover svg {
    stroke: #5c6bc0;
    color: #5c6bc0;
}

.dark-mode .share-buttons-container {
    border-top-color: #444;
}

.dark-mode .share-btn {
    background-color: #252526;
}

.dark-mode .share-btn svg {
    color: #d4d4d4;
}

.dark-mode .share-btn.whatsapp:hover {
    background-color: #1a3c2a;
}

.dark-mode .share-btn.twitter:hover {
    background-color: #1a3a4a;
}

.dark-mode .share-btn.generic:hover {
    background-color: #2a2d42;
    stroke: #a5d8ff;
}

/* ---------------- Referral Name Display (Auth Page) ---------------- */
.referral-name-display {
    display: none;
    /* Hidden by default */
    font-size: 13px;
    color: #0e639c;
    background-color: #e7f5ff;
    padding: 8px;
    border-radius: 6px;
    margin-top: 8px;
    text-align: center;
}

.dark-mode .referral-name-display {
    background-color: #1c3a50;
    color: #a5d8ff;
}

/* --- Animated Background --- */
.area {
    position: fixed; /* Use fixed to stay in viewport */
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1; /* Place it behind all content */
    pointer-events: none; /* Allow clicks to pass through */
}

.circles {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
    margin: 0;
    padding: 0;
}

.circles li {
    position: absolute;
    display: block;
    list-style: none;
    width: 20px;
    height: 20px;
    background: rgba(14, 99, 156, 0.2); /* Base color, JS will override opacity */
    /* Animation is now handled by JavaScript */
}

.circles li:nth-child(1) { left: 25%; width: 80px; height: 80px; }
.circles li:nth-child(2) { left: 10%; width: 20px; height: 20px; }
.circles li:nth-child(3) { left: 70%; width: 20px; height: 20px; }
.circles li:nth-child(4) { left: 40%; width: 60px; height: 60px; }
.circles li:nth-child(5) { left: 65%; width: 20px; height: 20px; }
.circles li:nth-child(6) { left: 75%; width: 110px; height: 110px; background: rgba(14, 99, 156, 0.15); }
.circles li:nth-child(7) { left: 35%; width: 150px; height: 150px; background: rgba(14, 99, 156, 0.1); }
.circles li:nth-child(8) { left: 50%; width: 25px; height: 25px; }
.circles li:nth-child(9) { left: 20%; width: 15px; height: 15px; background: rgba(14, 99, 156, 0.25); }
.circles li:nth-child(10) { left: 85%; width: 150px; height: 150px; background: rgba(14, 99, 156, 0.1); }

/* --- Animated Background Dark Mode Overrides --- */
.dark-mode .circles li {
    background: rgba(165, 216, 255, 0.1); /* Light blue accent for dark mode */
}
.dark-mode .circles li:nth-child(6) {
    background: rgba(165, 216, 255, 0.08);
}
.dark-mode .circles li:nth-child(7) {
    background: rgba(165, 216, 255, 0.05);
}
.dark-mode .circles li:nth-child(9) {
    background: rgba(165, 216, 255, 0.12);
}
.dark-mode .circles li:nth-child(10) {
    background: rgba(165, 216, 255, 0.05);
}

/* Dark mode animated background */
.dark-mode .animated-background {
    background: #1a1a1c;
}

