/* ---------------- Error Container (from 404.css) ---------------- */
.error-container {
    max-width: 450px;
    margin: 0 auto; /* Center the container */
    text-align: center;
    padding: 40px 20px;
}

.error-icon {
    font-size: 48px;
    margin-bottom: 20px;
}

.error-title {
    font-size: 6rem;
    font-weight: bold;
    color: #0e639c;
    margin: 0;
}

.error-subtitle {
    font-size: 1.5rem;
    color: #333;
    margin-bottom: 20px;
}

.error-message {
    color: #666;
    margin-bottom: 30px;
}

.dark-mode .error-subtitle, .dark-mode .error-message { color: #bbb; }

/* ===== AdBlocker Overlay Styles ===== */
#adblocker-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(14, 99, 156, 0.15);
    z-index: 10000;
    display: flex;
    align-items: center;
    justify-content: center;
    backdrop-filter: blur(10px);
}

.adblocker-container {
    background: #ffffff;
    border-radius: 16px;
    padding: 40px;
    max-width: 500px;
    width: 90%;
    text-align: center;
    box-shadow: 0 8px 32px rgba(14, 99, 156, 0.2);
    border: 3px solid #0e639c;
}

.adblocker-content h2 {
    color: #0e639c;
    margin-bottom: 20px;
    font-size: 28px;
    font-weight: 600;
}

.adblocker-icon {
    font-size: 64px;
    margin-bottom: 20px;
    filter: grayscale(0%) hue-rotate(200deg) saturate(1.2);
}

.adblocker-content p {
    color: #555;
    margin-bottom: 20px;
    line-height: 1.6;
}

.adblocker-content ul {
    text-align: left;
    color: #333;
    margin: 20px 0;
    padding-left: 0;
    list-style: none;
}

.adblocker-content li {
    margin: 10px 0;
    padding: 12px 20px;
    background: rgba(14, 99, 156, 0.08);
    border-radius: 10px;
    border-left: 4px solid #0e639c;
}

.adblocker-buttons {
    margin: 30px 0 20px;
    display: flex;
    gap: 15px;
    flex-wrap: wrap;
    justify-content: center;
}

.btn-refresh, .btn-upgrade {
    padding: 12px 24px;
    border: none;
    border-radius: 10px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    flex: 1;
    min-width: 140px;
}

.btn-refresh {
    background: #0e639c;
    color: white;
}

.btn-refresh:hover {
    background: #0b4f7a;
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(14, 99, 156, 0.3);
}

.btn-upgrade {
    background: #ff9800;
